import { useState, useEffect, FC } from 'react';
import CurrentWeather from './Components/CurrentWeather';
import './Styles/App.css';

interface WeatherResponse {
  coord: {
    lon: number;
    lat: number;
  };
  weather: {
    id: number;
    main: string;
    description: string;
    icon: string;
  }[];
  base: string;
  main: {
    temp: number;
    feels_like: number;
    temp_min: number;
    temp_max: number;
    pressure: number;
    humidity: number;
  };
  visibility: number;
  wind: {
    speed: number;
    deg: number;
  };
  clouds: {
    all: number;
  };
  dt: number;
  sys: {
    type: number;
    id: number;
    country: string;
    sunrise: number;
    sunset: number;
  };
  timezone: number;
  id: number;
  name: string;
  cod: number;
}

const apiKey = process.env.REACT_APP_API_KEY;
const url = `https://api.openweathermap.org/data/2.5/weather?lat=40.776764&lon=-111.877655&appid=${apiKey}&units=imperial`;

const App: React.FC = () => {
  const [weatherData, setWeatherData] = useState<WeatherResponse | null>(null);

  const apiRequest = async () => {
    try {
      const response = await fetch(url);
      const data: WeatherResponse = await response.json();
      setWeatherData(data);
      console.log(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    apiRequest();
  }, []);

  return (
    <div className="mainContainer">
      {/* <h1>TypeScript OpenWeather API Project</h1>
      {weatherData && (
        <div>
          <p>Location: {weatherData.name}</p>
          <p>Temperature: {weatherData.main.temp}Â°F</p>
          <p>Weather: {weatherData.weather[0].description}</p>
        </div>
      )} */}

    </div>
  );
};

export default App;
