import { useState, useEffect } from 'react';
import '../Styles/App.css';

interface WeatherResponse {
  coord: {
    lon: number;
    lat: number;
  };
  weather: {
    id: number;
    main: string;
    description: string;
    icon: string;
  }[];
  base: string;
  main: {
    temp: number;
    feels_like: number;
    temp_min: number;
    temp_max: number;
    pressure: number;
    humidity: number;
  };
  visibility: number;
  wind: {
    speed: number;
    deg: number;
  };
  clouds: {
    all: number;
  };
  dt: number;
  sys: {
    type: number;
    id: number;
    country: string;
    sunrise: number;
    sunset: number;
  };
  timezone: number;
  id: number;
  name: string;
  cod: number;
}

interface CurrentWeatherProps {
  lat: number;
  lon: number;
}

const CurrentWeather: React.FC<CurrentWeatherProps> = ({ lat, lon }) => {
  const [weatherData, setWeatherData] = useState<WeatherResponse | null>(null);
  const apiKey = process.env.REACT_APP_API_KEY;
  // const geoapifyApiKey = process.env.REACT_APP_GEOAPIFY_API_KEY;
  const geoapifyApiKey = process.env.REACT_APP_MAP_MARKER_API_KEY;
  const weatherUrl = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${apiKey}&units=imperial`;
  const mapUrl = `https://maps.geoapify.com/v1/view?apiKey=${geoapifyApiKey}&center=${lon},${lat}&zoom=14&style=osm-carto`;

  const apiRequest = async () => {
    try {
      const response = await fetch(weatherUrl);
      const data: WeatherResponse = await response.json();
      setWeatherData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    apiRequest();
  }, [lat, lon]);

  const convertUnixTimeStamp = (unix: number) => {
    let date = new Date(unix * 1000);
    let hours = date.getHours();
    let minutes = "0" + date.getMinutes();
    let seconds = "0" + date.getSeconds();
    let formattedTime = hours + ':' + minutes.substr(-2) + ':' + seconds.substr(-2);
    return formattedTime;
  }

  return (
    <div className="currentWeatherContainer">
      <h1 className="weatherCardHeader">Current Weather</h1>
      {weatherData && (
        <div>
          <h2 className="cityTitle">{weatherData.name}</h2>
          <iframe
            width="600"
            height="400"
            frameBorder="0"
            src={mapUrl}
            allowFullScreen
            title="Map"
          ></iframe>
          <div className="currentWeatherCardContainer">
            <div className="currentWeatherCard">
              <div className="weatherCardBody">
                <div className="cardText">
                  <div>Temperature: {weatherData.main.temp}째F</div>
                  <div>Weather: {weatherData.weather[0].description}</div>
                  <div>Feels like: {weatherData.main.feels_like}째F</div>
                  <div>High: {weatherData.main.temp_max}째F</div>
                  <div>Low: {weatherData.main.temp_min}째F</div>
                  <div>Humidity: {weatherData.main.humidity}%</div>
                  <div>Wind: {weatherData.wind.speed} MPH</div>
                  <div>Sunrise: {convertUnixTimeStamp(weatherData.sys.sunrise)} AM</div>
                  <div>Sunset: {convertUnixTimeStamp(weatherData.sys.sunset)} PM</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default CurrentWeather;
