import { useState, useEffect, FC } from 'react';
import CurrentWeather from './Components/CurrentWeather';
import SevenDayForecast from './Components/SevenDayForecast';
import './Styles/App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

interface WeatherResponse {
  coord: {
    lon: number;
    lat: number;
  };
  weather: {
    id: number;
    main: string;
    description: string;
    icon: string;
  }[];
  base: string;
  main: {
    temp: number;
    feels_like: number;
    temp_min: number;
    temp_max: number;
    pressure: number;
    humidity: number;
  };
  visibility: number;
  wind: {
    speed: number;
    deg: number;
  };
  clouds: {
    all: number;
  };
  dt: number;
  sys: {
    type: number;
    id: number;
    country: string;
    sunrise: number;
    sunset: number;
  };
  timezone: number;
  id: number;
  name: string;
  cod: number;
}

const App: React.FC = () => {
  const [lat, setLat] = useState<number>(40.776764); // Default latitude
  const [lon, setLon] = useState<number>(-111.877655); // Default longitude

  const handleLocationChange = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const formData = new FormData(event.currentTarget);
    const latitude = parseFloat(formData.get("latitude") as string);
    const longitude = parseFloat(formData.get("longitude") as string);
    setLat(latitude);
    setLon(longitude);
  };

  return (
    <div className="appMainContainer">
      <form onSubmit={handleLocationChange}>
        <label>
          Latitude:
          <input type="text" name="latitude" defaultValue={lat} />
        </label>
        <label>
          Longitude:
          <input type="text" name="longitude" defaultValue={lon} />
        </label>
        <button type="submit">Set Location</button>
      </form>
      <CurrentWeather lat={lat} lon={lon} />
      <SevenDayForecast lat={lat} lon={lon} />
    </div>
  );
};

export default App;
