import React, { useState, useEffect } from 'react';
import CurrentWeather from './Components/CurrentWeather';
import SevenDayForecast from './Components/SevenDayForecast';
import './Styles/App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

interface WeatherResponse {
  coord: {
    lon: number;
    lat: number;
  };
  weather: {
    id: number;
    main: string;
    description: string;
    icon: string;
  }[];
  base: string;
  main: {
    temp: number;
    feels_like: number;
    temp_min: number;
    temp_max: number;
    pressure: number;
    humidity: number;
  };
  visibility: number;
  wind: {
    speed: number;
    deg: number;
  };
  clouds: {
    all: number;
  };
  dt: number;
  sys: {
    type: number;
    id: number;
    country: string;
    sunrise: number;
    sunset: number;
  };
  timezone: number;
  id: number;
  name: string;
  cod: number;
}

const App: React.FC = () => {
  const [lat, setLat] = useState<number>(40.776764);
  const [lon, setLon] = useState<number>(-111.877655);

  useEffect(() => {
    // Fetch initial geolocation when component mounts
    navigator.geolocation.getCurrentPosition((position) => {
      const latitude = position.coords.latitude;
      const longitude = position.coords.longitude;
      setLat(latitude);
      setLon(longitude);
    });
  }, []);

  const handleLocationChange = () => {
    // Update geolocation when button is clicked
    navigator.geolocation.getCurrentPosition((position) => {
      const latitude = position.coords.latitude;
      const longitude = position.coords.longitude;
      setLat(latitude);
      setLon(longitude);
    });
  };

  return (
    <div className="appMainContainer">
      <div className="inlineForm">
        <button id="get-location" className="formButton" onClick={handleLocationChange}>
          Get Location
        </button>
        <div>
          <p>
            Latitude: <span id="latitude">{lat.toFixed(6)}</span>
          </p>
          <p>
            Longitude: <span id="longitude">{lon.toFixed(6)}</span>
          </p>
        </div>
      </div>
      <h1>TypeScript OpenWeather API Project</h1>
      <CurrentWeather lat={lat} lon={lon} />
      <SevenDayForecast lat={lat} lon={lon} />
    </div>
  );
};

export default App;
