import React, { useState } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import CurrentWeather from './Components/CurrentWeather';
import SevenDayForecast from './Components/SevenDayForecast';
import './Styles/App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

interface WeatherResponse {
  coord: {
    lon: number;
    lat: number;
  };
  weather: {
    id: number;
    main: string;
    description: string;
    icon: string;
  }[];
  base: string;
  main: {
    temp: number;
    feels_like: number;
    temp_min: number;
    temp_max: number;
    pressure: number;
    humidity: number;
  };
  visibility: number;
  wind: {
    speed: number;
    deg: number;
  };
  clouds: {
    all: number;
  };
  dt: number;
  sys: {
    type: number;
    id: number;
    country: string;
    sunrise: number;
    sunset: number;
  };
  timezone: number;
  id: number;
  name: string;
  cod: number;
}

const App: React.FC = () => {
  const [lat, setLat] = useState<number>(40.776764);
  const [lon, setLon] = useState<number>(-111.877655);

  const handleLocationChange = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const formData = new FormData(event.currentTarget);
    const latitude = parseFloat(formData.get("latitude") as string);
    const longitude = parseFloat(formData.get("longitude") as string);
    setLat(latitude);
    setLon(longitude);
  };

  return (
    <div className="appMainContainer">
      <Row className="mb-3">
        <Col sm={3}>
          <Form onSubmit={handleLocationChange}>
            <Form.Group controlId="latitude">
              <Form.Label>Latitude:</Form.Label>
              <Form.Control
                type="text"
                name="latitude"
                defaultValue={lat.toString()}
              />
            </Form.Group>
          </Form>
        </Col>
        <Col sm={3}>
          <Form onSubmit={handleLocationChange}>
            <Form.Group controlId="longitude">
              <Form.Label>Longitude:</Form.Label>
              <Form.Control
                type="text"
                name="longitude"
                defaultValue={lon.toString()}
              />
            </Form.Group>
          </Form>
        </Col>
        <Col sm={2}>
          <Button type="submit" className="mt-4">Set Location</Button>
        </Col>
      </Row>

      <CurrentWeather lat={lat} lon={lon} />
      <SevenDayForecast lat={lat} lon={lon} />
    </div>
  );
};

export default App;
